import React, { useState } from "react";
import "./WelcomePage.css";

const WelcomePage = ({ onGiftOpen }) => {
  const [isOpened, setIsOpened] = useState(false);

  const handleClick = () => {
    if (!isOpened) {
      setIsOpened(true);
      onGiftOpen();
    }
  };

  return (
    <div className="welcome-container">
      <Header />

      <div className="welcome-content">
        <h1>Welcome to Birthday Tracker!</h1>
        <p>Never miss a special day again</p>

        <div className="gift-button-container">
          <img
            src="/images/gift.png"
            alt="Gift"
            className={`gift-image ${isOpened ? "opened" : ""}`}
            onClick={handleClick}
          />
          {isOpened && (
            <div className="gift-sparkles">
              {[...Array(12)].map((_, i) => (
                <div key={i} className="sparkle"></div>
              ))}
            </div>
          )}
        </div>

        <p className="instruction">Click the gift to begin!</p>
      </div>
    </div>
  );
};

export default WelcomePage;
