import React, { useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarView.css";

const CalendarView = ({ birthdays }) => {
  const [date, setDate] = useState(new Date());

  const hasBirthdays = (date) => {
    return birthdays.some((bday) => {
      const bdayDate = new Date(bday.date);
      return (
        bdayDate.getDate() === date.getDate() &&
        bdayDate.getMonth() === date.getMonth()
      );
    });
  };

  console.log("Rendering calendar with birthdays:", birthdays);

  return (
    <div className="calendar-container">
      <Calendar
        onChange={setDate}
        value={date}
        tileClassName={({ date, view }) => {
          if (view !== "month") return null;
          let classes = [];
          if (date.toDateString() === new Date().toDateString()) {
            classes.push("today");
          }
          if (hasBirthdays(date)) classes.push("has-birthday");
          return classes.join(" ");
        }}
        tileContent={({ date, view }) => {
          if (view !== "month") return null;
          return hasBirthdays(date) ? (
            <div className="cake-emoji">ðŸŽ‚</div>
          ) : null;
        }}
        className="custom-calendar"
      />
    </div>
  );
};

export default CalendarView;
