import React, { useState, useEffect } from "react";
import "../styles/StarsBackground.css";

const StarsBackground = () => {
  const [stars, setStars] = useState([]);

  useEffect(() => {
    // Create stars with initial distribution
    const newStars = Array.from({ length: 80 }).map((_, i) => {
      const size = Math.random() * 4 + 3; // 3-7px size
      return {
        id: i,
        size,
        left: Math.random() * 100,
        // Distribute stars from bottom to middle initially
        top:  Math.random() * 50, // 50-100% of viewport
        duration: 10 + Math.random() * 15,
        delay: Math.random() * 3,
        opacity: 0.4 + Math.random() * 0.6,
        // Start some stars already moving
        progress: Math.random() * 50, // 0-50% animation progress
      };
    });
    setStars(newStars);
  }, []);

  return (
    <div className="stars-container">
      {stars.map((star) => (
        <div
          key={star.id}
          className="star"
          style={{
            "--size": `${star.size}px`,
            "--glow": `${star.size * 2}px`,
            left: `${star.left}%`,
            top: `calc(${star.top}% - (100vh * ${star.progress / 100}))`,
            animation: `floatUp ${star.duration}s linear ${star.delay}s infinite`,
            animationDelay: `${
              star.delay - (star.duration * star.progress) / 100
            }s`,
            opacity: star.opacity,
          }}
        />
      ))}
    </div>
  );
};

export default StarsBackground;
