import React, { useState, useEffect } from "react";
import "../styles/StarsBackground.css";

const StarsBackground = () => {
  const [stars, setStars] = useState([]);

  useEffect(() => {
    const newStars = Array.from({ length: 80 }).map((_, i) => {
      const size = Math.random() * 4 + 3; // 3-7px size
      const startPosition = 70 + Math.random() * 30; // Start in bottom 30% (70-100%)
      const animationProgress = Math.random() * 30; // 0-30% initial progress

      return {
        id: i,
        size,
        left: Math.random() * 100,
        startPosition, // Vertical starting point (70-100%)
        duration: 15 + Math.random() * 20, // 15-35s duration
        delay: Math.random() * 5,
        opacity: 0.4 + Math.random() * 0.6,
        animationProgress, // Some stars start slightly ascended
      };
    });
    setStars(newStars);
  }, []);

  return (
    <div className="stars-container">
      {stars.map((star) => (
        <div
          key={star.id}
          className="star"
          style={{
            "--size": `${star.size}px`,
            "--glow": `${star.size * 2}px`,
            left: `${star.left}%`,
            // Start in bottom 30% with some initial progress
            top: `calc(${star.startPosition}% - (100vh * ${
              star.animationProgress / 100
            }))`,
            animation: `floatUp ${star.duration}s linear ${star.delay}s infinite`,
            opacity: star.opacity,
          }}
        />
      ))}
    </div>
  );
};

export default StarsBackground;
