import React, { useState, useEffect } from "react";
import "../styles/StarsBackground.css";

const StarsBackground = () => {
  const [stars, setStars] = useState([]);

  useEffect(() => {
    // Create stars with larger base size
    const newStars = Array.from({ length: 100 }).map((_, i) => ({
      id: i,
      size: Math.random() * 4 + 2, // Size between 2-6px (larger than before)
      left: Math.random() * 100,
      top: 100 + Math.random() * 20,
      animationDuration: `${Math.random() * 20 + 15}s`,
      delay: `${Math.random() * 5}s`,
      opacity: Math.random() * 0.7 + 0.3,
      glowIntensity: Math.random() * 0.5 + 0.5, // Glow strength (0.5-1)
    }));
    setStars(newStars);
  }, []);

  return (
    <div className="stars-container">
      {stars.map((star) => (
        <div
          key={star.id}
          className="star"
          style={{
            "--size": `${star.size}px`,
            "--glow-size": `${star.size * 2}px`, // Glow proportional to star size
            "--glow-intensity": star.glowIntensity,
            left: `${star.left}%`,
            top: `${star.top}%`,
            animationDuration: star.animationDuration,
            animationDelay: star.delay,
            opacity: star.opacity,
          }}
        />
      ))}
    </div>
  );
};

export default StarsBackground;
