import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "./Header";
import "../styles/WelcomePage.css";

const WelcomePage = () => {
  const [isOpened, setIsOpened] = useState(false);
  const navigate = useNavigate();

  const handleGiftClick = () => {
    setIsOpened(true);
    setTimeout(() => {
      navigate("/app"); // Redirect to main app after animation
    }, 2000); // Match this with animation duration
  };

  return (
    <div className="welcome-container">
      <Header />

      <div className="welcome-content">
        <h1>Welcome to Birthday Tracker!</h1>
        <p>Never miss a special day again</p>

        <div
          className={`gift-box ${isOpened ? "opened" : ""}`}
          onClick={!isOpened ? handleGiftClick : null}
        >
          <div className="gift-lid"></div>
          <div className="gift-body">
            {isOpened && (
              <div className="gift-sparkles">
                {[...Array(12)].map((_, i) => (
                  <div key={i} className="sparkle"></div>
                ))}
              </div>
            )}
          </div>
          <div className="gift-bow"></div>
        </div>

        <p className="instruction">Click the gift to begin!</p>
      </div>
    </div>
  );
};

export default WelcomePage;
