import React, { useState, useEffect } from "react";
import "../styles/StarsBackground.css";

const StarsBackground = () => {
  const [stars, setStars] = useState([]);

  useEffect(() => {
    // Create 150 stars with guaranteed visibility
    const newStars = Array.from({ length: 150 }).map((_, i) => ({
      id: i,
      size: Math.random() * 4 + 3, // 3-7px size
      left: Math.random() * 100,
      top: 100 + Math.random() * 10, // Start below viewport
      duration: 20 + Math.random() * 25, // 20-45s
      delay: Math.random() * 5,
      opacity: 0.3 + Math.random() * 0.7,
    }));
    setStars(newStars);
  }, []);

  return (
    <div className="stars-container">
      {stars.map((star) => (
        <div
          key={star.id}
          className="star"
          style={{
            "--size": `${star.size}px`,
            "--glow": `${star.size * 2}px`,
            left: `${star.left}%`,
            top: `${star.top}%`,
            animation: `floatUp ${star.duration}s linear ${star.delay}s infinite`,
            opacity: star.opacity,
          }}
        />
      ))}
    </div>
  );
};

export default StarsBackground;
