import React, { useState, useRef } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarView.css";

const CalendarView = ({ birthdays }) => {
  const [selectedDate, setSelectedDate] = useState(null);
  const calendarRef = useRef(null);

  // Get birthdays for a specific date
  const getDateBirthdays = (date) => {
    return birthdays.filter((bday) => {
      const bdayDate = new Date(bday.date);
      return (
        bdayDate.getDate() === date.getDate() &&
        bdayDate.getMonth() === date.getMonth()
      );
    });
  };

  const handleDateClick = (value) => {
    const dateBirthdays = getDateBirthdays(value);
    if (dateBirthdays.length > 0) {
      // Find the tile element in the DOM
      const tiles = calendarRef.current.querySelectorAll(
        ".react-calendar__tile"
      );
      const tile = Array.from(tiles).find((tile) => {
        const tileDate = new Date(tile.getAttribute("aria-label"));
        return (
          tileDate.getDate() === value.getDate() &&
          tileDate.getMonth() === value.getMonth() &&
          tileDate.getFullYear() === value.getFullYear()
        );
      });

      if (tile) {
        const tileRect = tile.getBoundingClientRect();
        setSelectedDate({
          date: value,
          birthdays: dateBirthdays,
          position: {
            left: tileRect.left + window.scrollX + tileRect.width / 2,
            top: tileRect.top + window.scrollY - 10,
          },
        });
      }
    } else {
      setSelectedDate(null);
    }
  };

  const closePopup = () => {
    setSelectedDate(null);
  };

  return (
    <div className="calendar-container">
      <Calendar
        ref={calendarRef}
        tileContent={({ date, view }) => {
          if (view !== "month") return null;
          const dateBirthdays = getDateBirthdays(date);
          return dateBirthdays.length > 0 ? (
            <div className="cake-emoji">ðŸŽ‚</div>
          ) : null;
        }}
        tileClassName={({ date, view }) => {
          if (view !== "month") return null;
          const dateBirthdays = getDateBirthdays(date);
          return dateBirthdays.length > 0 ? "has-birthday" : "";
        }}
        onClickDay={handleDateClick}
        className="custom-calendar"
      />

      {selectedDate && (
        <>
          <div className="popup-overlay" onClick={closePopup} />
          <div
            className="birthday-popup"
            style={{
              left: `${selectedDate.position.left}px`,
              top: `${selectedDate.position.top}px`,
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <button className="close-popup" onClick={closePopup}>
              Ã—
            </button>
            {selectedDate.birthdays.map((bday) => (
              <div key={bday.id} className="birthday-popup-item">
                <img
                  src={bday.photo}
                  alt={bday.name}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "/images/default.jpg";
                  }}
                />
                <span>{bday.name}</span>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default CalendarView;
