import React, { useState } from "react";
import "../styles/WelcomePage.css";
import Confetti from "react-confetti";
import { useNavigate } from "react-router-dom";
import ""

const WelcomePage = ({ onGiftOpen }) => {
  const [isOpened, setIsOpened] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [confettiSize, setConfettiSize] = useState({ width: 0, height: 0 });
  const navigate = useNavigate();

  // Sparkle positions and sizes
  const sparklePositions = [
    { tx: "-40px", ty: "-60px", delay: "0s", size: "8px" },
    { tx: "30px", ty: "-80px", delay: "0.1s", size: "6px" },
    { tx: "-20px", ty: "-120px", delay: "0.2s", size: "10px" },
    { tx: "50px", ty: "-40px", delay: "0.3s", size: "7px" },
    { tx: "-60px", ty: "-90px", delay: "0.4s", size: "9px" },
    { tx: "10px", ty: "-110px", delay: "0.5s", size: "5px" },
    { tx: "40px", ty: "-70px", delay: "0.6s", size: "8px" },
    { tx: "-30px", ty: "-50px", delay: "0.7s", size: "6px" },
    { tx: "20px", ty: "-100px", delay: "0.8s", size: "7px" },
    { tx: "-50px", ty: "-80px", delay: "0.9s", size: "9px" },
    { tx: "60px", ty: "-60px", delay: "1s", size: "5px" },
    { tx: "-10px", ty: "-90px", delay: "1.1s", size: "8px" },
  ];

  const handleClick = () => {
    if (!isOpened) {
      setIsOpened(true);
      setConfettiSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
      setShowConfetti(true);

      setTimeout(() => {
        onGiftOpen();
        navigate("/App"); // Change "/main" to your actual main page route
      }, 3000); // Adjust time to match confetti duration
    }
  };

  return (
    <div className="welcome-container">
      {showConfetti && (
        <Confetti
          width={confettiSize.width}
          height={confettiSize.height}
          recycle={false}
          numberOfPieces={500}
          gravity={0.2}
          colors={["#fef4b5", "#a332d5", "#f9547a", "#ff6857", "#f98c53"]}
          style={{ position: "fixed" }}
        />
      )}

      <div className="welcome-content">
        <h1>Welcome to Birthday Reminder!</h1>
        <p>Never miss a special day again</p>

        <div className="gift-button-container">
          <img
            src="/images/gift.png"
            alt="Click to open gift"
            className={`gift-image ${isOpened ? "opened" : ""}`}
            onClick={handleClick}
          />

          {isOpened && (
            <div className="gift-sparkles">
              {sparklePositions.map((sparkle, index) => (
                <div
                  key={index}
                  className="sparkle"
                  style={{
                    "--tx": sparkle.tx,
                    "--ty": sparkle.ty,
                    "--size": sparkle.size,
                    animationDelay: sparkle.delay,
                    width: sparkle.size,
                    height: sparkle.size,
                  }}
                />
              ))}
            </div>
          )}
        </div>

        <p className="instruction">Click the gift to begin!</p>
      </div>
    </div>
  );
};

export default WelcomePage;
