import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const EditBirthday = ({ birthdays, updateBirthday }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    nickname: "",
    date: "",
    photo: "",
    zodiac: "",
    giftIdeas: "",
    hobbies: "",
    favoriteColor: "",
    notes: "",
  });

  // Pre-fill form with existing data
  useEffect(() => {
    const birthdayToEdit = birthdays.find((b) => b.id === parseInt(id));
    if (birthdayToEdit) {
      setFormData({
        ...birthdayToEdit,
        date: birthdayToEdit.date.split("T")[0], // Format date for input
      });
    }
  }, [id, birthdays]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateBirthday(parseInt(id), formData);
    navigate(`/birthday/${id}`);
  };

  return (
    <div className="edit-birthday-container">
        <div className="edit-birthday-panel"></div>
      <h2>Edit Birthday</h2>
      <form onSubmit={handleSubmit} className="birthday-form">
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Nickname:</label>
          <input
            type="text"
            name="nickname"
            value={formData.nickname}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Birth Date:</label>
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Photo URL:</label>
          <input
            type="url"
            name="photo"
            value={formData.photo}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Zodiac Sign:</label>
          <input
            type="text"
            name="zodiac"
            value={formData.zodiac}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Gift Ideas:</label>
          <textarea
            name="giftIdeas"
            value={formData.giftIdeas}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Hobbies:</label>
          <textarea
            name="hobbies"
            value={formData.hobbies}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Favorite Color:</label>
          <input
            type="text"
            name="favoriteColor"
            value={formData.favoriteColor}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Notes:</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
          />
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={() => navigate(-1)}
            className="styled-button cancel"
          >
            Cancel
          </button>
          <button type="submit" className="styled-button submit">
            Save Changes
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditBirthday;
