// In CalendarView.js
import { Calendar, Badge } from "antd"; // Or use custom indicators
import "../styles/CalendarView.css";

const CalendarView = ({ birthdays }) => {
  const dateCellRender = (value) => {
    const dateStr = value.format("YYYY-MM-DD");
    const dayEvents = birthdays.filter(
      (bday) => new Date(bday.date).toISOString().split("T")[0] === dateStr
    );

    return (
      <div className="events">
        {dayEvents.map((event) => (
          <Badge
            key={event.id}
            color="#a332d5" // Your purple color
            text={<span className="event-name">{event.name}</span>}
          />
        ))}
      </div>
    );
  };

  return (
    <div className="calendar-container">
      <Calendar dateCellRender={dateCellRender} className="custom-calendar" />
    </div>
  );
};
