import React, { useState, useEffect } from "react";
import "../styles/StarsBackground.css";

const StarsBackground = () => {
  const [stars, setStars] = useState([]);

  useEffect(() => {
    // Create 120 stars (good balance between density and performance)
    const newStars = Array.from({ length: 120 }).map((_, i) => ({
      id: i,
      size: Math.random() * 3 + 1,
      left: Math.random() * 100,
      top: 100 + Math.random() * 20, // Start below viewport (100-120%)
      animationDuration: `${Math.random() * 20 + 15}s`, // Slower movement (15-35s)
      delay: `${Math.random() * 5}s`,
      opacity: Math.random() * 0.8 + 0.2, // Brighter base opacity
      glow: Math.random() * 3 + 2, // Stronger glow
    }));
    setStars(newStars);
  }, []);

  return (
    <div className="stars-container">
      {stars.map((star) => (
        <div
          key={star.id}
          className="star"
          style={{
            "--size": `${star.size}px`,
            "--glow": `${star.glow}px`,
            left: `${star.left}%`,
            top: `${star.top}%`,
            animationDuration: star.animationDuration,
            animationDelay: star.delay,
            opacity: star.opacity,
          }}
        />
      ))}
    </div>
  );
};

export default StarsBackground;
