import React, { useState, useEffect } from "react";
import "../styles/StarsBackground.css";

const StarsBackground = () => {
  const [stars, setStars] = useState([]);

  useEffect(() => {
    // Create 70 sparkle stars
    const newStars = Array.from({ length: 70 }).map((_, i) => ({
      id: i,
      size: Math.random() * 4 + 4, // 4-8px size (larger for detail)
      left: Math.random() * 100,
      top: 100 + Math.random() * 20,
      duration: 12 + Math.random() * 18, // 12-30s
      delay: Math.random() * 4,
      opacity: 0.4 + Math.random() * 0.6,
      rotation: Math.random() * 360, // Random initial rotation
    }));
    setStars(newStars);
  }, []);

  return (
    <div className="stars-container">
      {stars.map((star) => (
        <div
          key={star.id}
          className="sparkle"
          style={{
            "--size": `${star.size}px`,
            "--rotation": `${star.rotation}deg`,
            left: `${star.left}%`,
            top: `${star.top}%`,
            animation: `floatUp ${star.duration}s linear ${
              star.delay
            }s infinite, 
                       twinkle ${3 + Math.random() * 4}s ease-in-out infinite`,
            opacity: star.opacity,
          }}
        >
          <div className="sparkle-core"></div>
          <div className="sparkle-ray"></div>
          <div className="sparkle-ray" style={{ "--angle": "45deg" }}></div>
          <div className="sparkle-ray" style={{ "--angle": "90deg" }}></div>
          <div className="sparkle-ray" style={{ "--angle": "135deg" }}></div>
        </div>
      ))}
    </div>
  );
};

export default StarsBackground;
