import React, { useState, useEffect } from "react";
import "./StarsBackground.css";

const StarsBackground = () => {
  const [stars, setStars] = useState([]);

  useEffect(() => {
    // Create 200 stars with guaranteed movement
    const newStars = Array.from({ length: 200 }).map((_, i) => {
      const size = Math.random() * 5 + 3; // 3-8px size
      return {
        id: i,
        size,
        left: Math.random() * 100,
        top: 100 + Math.random() * 10, // Start just below viewport
        animationDuration: `${Math.random() * 25 + 20}s`, // 20-45s duration
        delay: `${Math.random() * 10}s`, // 0-10s delay
        opacity: Math.random() * 0.7 + 0.3,
        glowSize: size * 3, // Glow proportional to size
      };
    });
    setStars(newStars);
  }, []);

  return (
    <div className="stars-container">
      {stars.map((star) => (
        <div
          key={star.id}
          className="star"
          style={{
            "--size": `${star.size}px`,
            "--glow": `${star.glowSize}px`,
            left: `${star.left}%`,
            top: `${star.top}%`,
            animation: `floatUp ${star.animationDuration} linear ${star.delay} infinite`,
            opacity: star.opacity,
          }}
        />
      ))}
    </div>
  );
};

export default StarsBackground;
